var http = require('http')
  , url = require('url');

function start(route, handle) {
  function onRequest (req, res) {
    var pathname = url.parse(req.url).pathname
      , postData = '';
    console.log('Request for ' + pathname + ' received.');

    req.setEncoding('utf8');

    req.addListener('data', function (postDataChunck) {
      postData += postDataChunck;
      console.log('Received POST data chunck ' + postDataChunck);
    });

    req.addListener('end', function () {
      route(handle, pathname, res, postData);
    });
  }

  http.createServer(onRequest).listen(8888, function () {
    console.log('Server running in port 8888');
  });
}

exports.start = start;
