var querystring = require('querystring')
  , fs = require('fs');

function start (res, data) {
  console.log('RequestHandler ~start~ was called...');
  var body = '<html>'+
    '<head>'+
    '<meta http-equiv="Content-Type" '+
    'content="text/html; charset=UTF-8" />'+
    '</head>'+
    '<body>'+
    '<form action="/upload" method="post">'+
    '<textarea name="text" rows="20" cols="60"></textarea>'+
    '<input type="submit" value="Submit text" />'+
    '</form>'+
    '</body>'+
    '</html>';
  res.writeHead(200, {'content-type': 'text/html'});
  res.wrte(body);
  res.end();
}

function upload (res, data) {
  console.log('RequestHandler ~upload~ was called...');
  res.writeHead(200, {'content-type': 'text/plain'});
  res.write('You\'ve sent the text: ' + querystring.parse(data).text);
  res.end();
}

function show (res, data) {
  console.log('RequestHandler ~show~ was called...');
  fs.readFile('/tmp/test.png', 'binary', function (error, file) {
    if (error) {
      res.writeHead(500, {'content-type': 'text/plain'});
      res.write(err + '\n');
      res.end();
    } else {
      res.writeHead(200, {'content-type': 'image/png'});
      res.write(file, 'binary');
      res.end();
    }
  });
}

exports.start = start;
exports.upload = upload;
exports.show = show;

